import React from "react";
import "./EmailFooter.css";
import emailjs from "@emailjs/browser";


function EmailFooter() {
  // Xử lý js

//   Gọi api emailjs
  useEffect(() => {
    emailjs.init("w15JVV1AB96-f0lko"); // Thay publicKey của bạn
  }, []);

  // html
  return (
    <div className="EmailFooter">
      <h4>Đăng kí lái thử và báo giá</h4>

      <hr />
      <p>Quý khách vui lòng nhập thông tin và chọn hình thức thanh toán</p>

      <label>Trả thẳng</label>
      <input type="radio" />

      <label>Trả góp</label>
      <input type="radio" />

      <input class="form-control" placeholder="Họ và tên" type="text" />
      <input class="form-control" placeholder="Nhập số điện thoại" type="text" />

      <select class="form-control" id="" >
        <option value="">Chọn loại xe</option>
        <option value="XL7">Xl7</option>
        <option value="Jimmy">Jimmy</option>
        <option value="Blind Van">Blind Van</option>
      </select>

      <button class="btn btn_EmailFooter">
        Gửi yêu cầu
      </button>
    </div>
  );
}

export default EmailFooter;
