import React, { useState } from "react";
import CallButton from "./CallButton"; // Import component CallButton
import PopupEmail from "./PopupEmail"; // Import component PopupEmail

const ParentComponent = () => {
  const [isVisible, setIsVisible] = useState(true); // Mặc định isVisible là false

  const toggleVisibility = () => {
    setIsVisible((prev) => !prev); // Lật lại giá trị của isVisible
  };

  return (
    <div>
      <CallButton onToggle={toggleVisibility} /> {/* Truyền hàm toggle vào CallButton */}
      <PopupEmail isVisible={isVisible} /> {/* Truyền prop isVisible vào PopupEmail */}
    </div>
  );
};

export default ParentComponent;
