import React, { useState, useEffect } from 'react';

function MainComponent() {
  const [products, setProducts] = useState([]);
  const [banner, setBanner] = useState([]);
  const [news, setNews] = useState([]);

  // Gọi API để lấy danh sách sản phẩm
  const callAPIRenderProduct = () => {
    fetch("http://localhost:3000/productsSUV")
      .then((response) => response.json())
      .then((dataApiList1) => {
        fetch("http://localhost:3000/productsBEN")
          .then((response) => response.json())
          .then((dataApiList2) => {
            setProducts([...dataApiList1, ...dataApiList2]);
          });
      })
      .catch((error) => {
        console.error("Đã có lỗi khi gọi API:", error);
      });
  };

  // Gọi API để lấy banner
  const callAPIRenderBanner = () => {
    fetch("http://localhost:3000/banner")
      .then((response) => response.json())
      .then((data) => setBanner(data))
      .catch((error) => console.error("Đã xảy ra lỗi:", error));
  };

  // Gọi API để lấy tin tức
  const callAPIRenderNews = () => {
    fetch("http://localhost:3000/news")
      .then((response) => response.json())
      .then((data) => setNews(data))
      .catch((error) => console.error("Lỗi khi lấy tin tức:", error));
  };

  // Sử dụng useEffect để gọi API khi component mount
  useEffect(() => {
    callAPIRenderProduct();
    callAPIRenderBanner();
    callAPIRenderNews();
  }, []);

  return (
    <div>
      <div className="main_1_renderProduct">
        {products.map((product) => (
          <div className="col-md-3" key={product.id}>
            <div className="card">
              <img className="card-img-top" src={product.img} alt={product.nameCar} />
              <h3 className="card-title">{product.nameCar}</h3>
              <div className="main_1_renderProduct_Action d-flex align-items-center">
                <span className="card-title">Từ {product.price} VND</span>
                <button className="justify-content-end card_button">
                  <i className="bx bx-right-arrow-alt"></i>
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="imgBanner">
        {banner.map((item, index) => (
          <img src={item.imgBanner} alt="Banner" key={index} />
        ))}
      </div>

      <div className="main_5_news">
        {news.map((item, index) => (
          <div className="col-md-3" key={index}>
            <div className="card">
              <img className="card-img-top" src={item.img} alt="News" />
              <p className="card-text">{item.describe_news}</p>
              <a className="describe_a" href="#">
                <span>Xem chi tiết</span>
                <button className="describe_button">
                  <i className="bx bx-right-arrow-alt"></i>
                </button>
              </a>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MainComponent;
