// CallButton.js
import React from "react";
import "./CallButton.css"; // Đảm bảo rằng bạn có file CSS cho nút này

const CallButton = () => {
  const phoneNumber = "0909260991"; // Truyền trực tiếp số điện thoại ở đây

  return (
    <a href={`tel:${phoneNumber}`} className="call-button">
      <img src="/images/icon_phone.png" alt="Call" /> {/* Đường dẫn tới biểu tượng */}
    </a>
  );
};

export default CallButton;
