import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const ProductDetails_test = () => {
  const { id } = useParams(); // Nhận id từ URL
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // Gọi API để lấy chi tiết sản phẩm dựa trên id
    fetch(`http://localhost:3000/productsSUV/${id}`)
      .then((response) => response.json())
      .then((data) => setProduct(data))
      .catch((error) => console.error("Lỗi khi lấy chi tiết sản phẩm:", error));
  }, [id]);

  if (!product) return <p>Đang tải...</p>;

  return (
    <div>
      <h2>Chi tiết sản phẩm</h2>
      <h3>{product.name}</h3>
      <p>Giá: {product.price}</p>
      <p>Mô tả: {product.description}</p>
      {/* Hiển thị thêm thông tin chi tiết khác */}
    </div>
  );
};

export default ProductDetails_test;
