import React, { useState } from "react";
import "./CallButton.css"; // Đảm bảo rằng bạn có file CSS cho nút này

const CallButton = ({onButtonClick}) => {
  const phoneNumber = "0912345678"; // Số điện thoại

  //  xử lý cho trạng thái css

  // mảng này chứa 2 trạng thái của nút bấm
  const [isButtonClicked, setIsButtonClicked] = useState(false);

  // hàm xử lý
  const CssPopUpEmail = () => {
      setIsButtonClicked(true);
      onButtonClick(true);
  }

  return (
    <div className="call-buttons-container">
      {/* Nút thay đổi thuộc tính của PopupEmail */}
      <button className="call-button-email" onClick={CssPopUpEmail}>
        <div className="button-background-email"></div> {/* Nền có hiệu ứng */}
        <img className="img_icon" src="/images/icon.png" alt="Call" />
      </button>

      {/* Nút gọi 2 */}
      <a href={`tel:${phoneNumber}`} className="call-button-call">
        <div className="button-background-call"></div> {/* Nền có hiệu ứng */}
        <img className="img_icon" src="/images/icon_phone.png" alt="Call" />
        {/* Đường dẫn tới biểu tượng */}
      </a>
    </div>
  );
};

export default CallButton;
