import React from 'react';

const CarDetail = () => {
  // Hàm xử lý khi nhấn vào sản phẩm
  const viewProductDetails = (productId) => {
    console.log("ID của sản phẩm:", productId);

    // Gọi API với id sản phẩm
    fetch(`http://your-backend-api.com/car/${productId}`)
      .then((response) => response.json())
      .then((data) => {
        // Xử lý dữ liệu chi tiết của sản phẩm
        console.log("Dữ liệu sản phẩm:", data);
      })
      .catch((error) => console.error("Lỗi khi lấy chi tiết sản phẩm:", error));
  };

  // Dữ liệu sản phẩm giả định để hiển thị
  const products = [
    { id: 234, name: 'Toyota Camry', price: '500 triệu' },
    { id: 2, name: 'Honda Civic', price: '600 triệu' },
    { id: 3, name: 'Ford Ranger', price: '700 triệu' },
  ];

  return (
    <div>
      <h2>Danh sách các sản phẩm</h2>
      <div className="product-list">
        {products.map((product) => (
          <div
            key={product.id}
            className="product"
            onClick={() => viewProductDetails(product.id)}
          >
            <h3>Sản phẩm {product.id}: {product.name}</h3>
            <p>Giá: {product.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CarDetail;
