import React, { useState } from "react";
import CallButton from "./CallButton"; // Import component CallButton
import PopupEmail from "./PopupEmail"; // Import component PopupEmail

const ParentComponent = () => {
  const [cssStatus, setIsVisible] = useState(false); // Khởi tạo giá trị cssStatus là false

  const SetStatus = () => {
    console.log("Gọi hàm SetStatus"); // Log để kiểm tra xem hàm có được gọi
    setIsVisible((prevStatus) => !prevStatus); // Lật lại giá trị của cssStatus
  };

  return (
    <div>
      <CallButton SetStatus={SetStatus} /> {/* Truyền SetStatus vào CallButton */}
      <PopupEmail cssStatus={cssStatus} /> {/* Truyền cssStatus vào PopupEmail */}
    </div>
  );
};

export default ParentComponent;
