  const [cssStatus, setIsVisible] = useState(false); // Mặc định isVisible là false

  const toggleVisibility = () => {
    setIsVisible((cssStatus) => !cssStatus); // Lật lại giá trị của cssStatus
  };

  return (
    <div>
      {/* <CallButton onToggle={toggleVisibility} /> Truyền hàm toggle vào CallButton */}
      <PopupEmail cssStatus={cssStatus} /> {/* Truyền prop cssStatus vào PopupEmail */}
    </div>
  );
};

export default ParentComponent;
